import React from 'react';
import { List, ListItem, ListItemText, Typography, Box, styled } from '@mui/material';

const ListItemStyled = styled(ListItem)(({ theme }) => ({
  backgroundColor: theme.palette.background.paper,
  padding: theme.spacing(1),
  borderRadius: theme.spacing(1),
  marginBottom: theme.spacing(1),
}));

const FileHashTypography = styled(Typography)(({ theme }) => ({
  fontWeight: 'bold',
}));

const UploadedFilesList = ({ uploadedFiles }) => {
  return (
    <Box mt={2}>
      <Typography variant="h6" gutterBottom>
        Uploaded Files
      </Typography>
      <List>
        {uploadedFiles.map((file, index) => (
         <ListItemStyled key={index}>
         <ListItemText
           primary={
             <React.Fragment>
               <FileHashTypography>File Name: {file.fileName}</FileHashTypography>
               <br />
               File Size:{' '}
               {file.fileSize < 1024 * 1024
                 ? `${(file.fileSize / 1024).toFixed(2)} KB`
                 : `${(file.fileSize / (1024 * 1024)).toFixed(2)} MB`}
             </React.Fragment>
           }
         />
       </ListItemStyled>
        ))}
      </List>
    </Box>
  );
};

export default UploadedFilesList;
