import React, { useState } from "react";
import {
  Box,
  Typography,
  List,
  ListItem,
  ListItemText,
  Button,
  CircularProgress,
  Grid,
  Card,
  CardContent,
  Divider,
  styled,
} from "@mui/material";

const StyledBox = styled(Box)(({ theme }) => ({
  padding: theme.spacing(2),
}));

const StyledCard = styled(Card)(({ theme }) => ({
  backgroundColor: theme.palette.background.paper,
  padding: theme.spacing(2),
  borderRadius: theme.spacing(1),
  marginBottom: theme.spacing(2),
}));

const StyledFileName = styled(Typography)(({ theme }) => ({
  fontWeight: "bold",
  marginBottom: theme.spacing(1),
}));

const StyledFileSize = styled(Typography)(({ theme }) => ({
  marginBottom: theme.spacing(1),
}));

const StyledDownloadButton = styled(Button)(({ theme }) => ({
  marginTop: theme.spacing(1),
}));

const UploadedFilesList = ({ uploadedFiles }) => {
  const [downloadProgress, setDownloadProgress] = useState({});

  const handleDownload = async (
    fileName,
    fileHash,
    fileSize,
    fileExtension
  ) => {
    try {
      setDownloadProgress((prevState) => ({
        ...prevState,
        [fileHash]: 0,
      }));
      const response = await fetch(
        `http://localhost:8000/api/retrieve/${fileHash}?fileSize=${fileSize}&fileExtension=${fileExtension}&fileName=${fileName}`,
        {
          method: "GET",
          responseType: "blob",
          onProgress: (event) => {
            if (event.lengthComputable) {
              const progress = Math.round((event.loaded / event.total) * 100);
              setDownloadProgress((prevState) => ({
                ...prevState,
                [fileHash]: progress,
              }));
            }
          },
        }
      );

      if (response.ok) {
        const blob = await response.blob();
        const downloadUrl = window.URL.createObjectURL(blob);
        const link = document.createElement("a");
        link.href = downloadUrl;
        link.download = `${fileName}`;
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
      } else {
        console.error("Error downloading file");
      }
    } catch (error) {
      console.error("Error downloading file:", error);
    } finally {
      setDownloadProgress((prevState) => ({
        ...prevState,
        [fileHash]: 0,
      }));
    }
  };

  return (
    <StyledBox>
      <Typography variant="h5" gutterBottom>
        Uploaded Files
      </Typography>
      <List>
        {uploadedFiles.map((file, index) => (
          <StyledCard key={index}>
            <CardContent>
              <StyledFileName variant="h6" gutterBottom>
                {file.fileName}
              </StyledFileName>
              <StyledFileSize variant="body2" color="textSecondary">
                File Size: {(file.fileSize / (1024 * 1024)).toFixed(2)} MB
              </StyledFileSize>
              <Divider />
              <Grid container justifyContent="flex-end">
                <Grid item>
                  <StyledDownloadButton
                    variant="contained"
                    color="primary"
                    onClick={() =>
                      handleDownload(
                        file.fileName,
                        file.fileHash,
                        file.fileSize,
                        file.fileExtension
                      )
                    }
                    disabled={downloadProgress[file.fileHash] > 0}
                  >
                    {downloadProgress[file.fileHash] > 0 ? (
                      <CircularProgress
                        variant="determinate"
                        value={downloadProgress[file.fileHash]}
                        size={24}
                      />
                    ) : (
                      "Download"
                    )}
                  </StyledDownloadButton>
                </Grid>
              </Grid>
            </CardContent>
          </StyledCard>
        ))}
      </List>
    </StyledBox>
  );
};

export default UploadedFilesList;
