import React, { useState } from "react";
import {
  List,
  ListItem,
  ListItemText,
  Typography,
  Box,
  styled,
  CircularProgress,
  Button,
} from "@mui/material";

const ListItemStyled = styled(ListItem)(({ theme }) => ({
  backgroundColor: theme.palette.background.paper,
  padding: theme.spacing(1),
  borderRadius: theme.spacing(1),
  marginBottom: theme.spacing(1),
}));

const FileHashTypography = styled(Typography)(({ theme }) => ({
  fontWeight: "bold",
}));

const UploadedFilesList = ({ uploadedFiles }) => {
  const [downloadProgress, setDownloadProgress] = useState({});
//   console.log(uploadedFiles.fileHash, uploadedFiles.fileSize, uploadedFiles.fileExtension, uploadedFiles.fileSize);
  const handleDownload = async (fileName, fileHash, fileSize, fileExtension) => {
    try {
      setDownloadProgress((prevState) => ({
        ...prevState,
        [fileHash]: 0,
      }));
      console.log(fileExtension);
      const response = await fetch(
        `http://localhost:8000/api/retrieve/${fileHash}?fileSize=${fileSize}&fileExtension=${fileExtension}&fileName=${fileName}`,
        {
          method: "GET",
          responseType: "blob",
          onProgress: (event) => {
            const progress = Math.round((event.loaded / event.total) * 100);
            setDownloadProgress((prevState) => ({
              ...prevState,
              [fileHash]: progress,
            }));
          },
        }
      );

      if (response.ok) {
        const blob = await response.blob();
        const downloadUrl = window.URL.createObjectURL(blob);
        const link = document.createElement("a");
        link.href = downloadUrl;
        link.download = `${fileHash}${fileExtension}`;
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
      } else {
        console.error("Error downloading file");
      }
    } catch (error) {
      console.error("Error downloading file:", error);
    } finally {
      setDownloadProgress((prevState) => ({
        ...prevState,
        [fileHash]: 0,
      }));
    }
  };

  return (
    <Box mt={2}>
      <Typography variant="h6" gutterBottom>
        Uploaded Files
      </Typography>
      <List>
        {uploadedFiles.map((file, index) => (
          <ListItemStyled key={index}>
            <ListItemText
              primary={
                <React.Fragment>
                  <FileHashTypography>
                    File Name: {file.fileName}
                  </FileHashTypography>
                  <br />
                  File Size: {(file.fileSize / (1024 * 1024)).toFixed(2)} MB
                </React.Fragment>
              }
              secondary={
                <React.Fragment>
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={() =>
                      handleDownload(
                        file.fileName,
                        file.fileHash,
                        file.fileSize,
                        file.fileExtension
                      )
                    }
                  >
                    Download
                  </Button>
                  {downloadProgress[file.fileHash] > 0 && (
                    <CircularProgress
                      variant="determinate"
                      value={downloadProgress[file.fileHash]}
                      size={24}
                      style={{ marginLeft: "8px" }}
                    />
                  )}
                </React.Fragment>
              }
            />
          </ListItemStyled>
        ))}
      </List>
    </Box>
  );
};

export default UploadedFilesList;
