import React, { useState } from 'react';
import {
  Box,
  Button,
  CircularProgress,
  FormControl,
  FormLabel,
  Input,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
} from '@material-ui/core';

const UploadedFilesList = ({ files }) => {
  const handleDownload = (file) => {
    // Your logic to download the file goes here
  };

  return (
    <List>
      {files.map((file) => (
        <ListItem key={file.id}>
          <ListItemText primary={file.name} secondary={`Size: ${file.size} bytes`} />
          <ListItemSecondaryAction>
            <Button onClick={() => handleDownload(file)}>Download</Button>
          </ListItemSecondaryAction>
        </ListItem>
      ))}
    </List>
  );
};

const FileUpload = () => {
  const [files, setFiles] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [downloading, setDownloading] = useState(false);
  const [downloadProgress, setDownloadProgress] = useState(0);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setFiles([...files, { id: Date.now(), name: file.name, size: file.size }]);
  };

  const handleUpload = () => {
    setUploading(true);
    const interval = setInterval(() => {
      setUploadProgress((prevProgress) => {
        if (prevProgress === 100) {
          clearInterval(interval);
          setUploading(false);
        }
        return prevProgress + 10;
      });
    }, 500);
  };

  const handleDownload = (file) => {
    setDownloading(true);
    const interval = setInterval(() => {setDownloadProgress((prevProgress) => {
        if (prevProgress === 100) {
          clearInterval(interval);
          setDownloading(false);
        }
        return prevProgress + 10;
      });
    }, 500);
  };

  return (
    <Box>
      <FormControl>
        <FormLabel>Upload a file</FormLabel>
        <Input
          type="file"
          onChange={handleFileChange}
          endAdornment={
            uploading && (
              <CircularProgress size={20} thickness={2} style={{ position: 'absolute', right: '10px', top: '50%', transform: 'translateY(-50%)' }} />
            )
          }
        />
        <Button onClick={handleUpload}>Upload</Button>
      </FormControl>
      <UploadedFilesList files={files} />
      <Box>
        {downloading && (
          <CircularProgress size={20} thickness={2} style={{ marginRight: '10px' }} value={downloadProgress} />
        )}
        <Button onClick={() => handleDownload(files[0])}>Download first file</Button>
      </Box>
    </Box>
  );
};

export default FileUpload;
