import React, { useRef, useState } from "react";
import "./FileUpload.css";
import axios from "axios";
import UploadedFilesList from "./UploadedFilesList";
import { useNavigate } from "react-router-dom";
import { signOut } from "firebase/auth";
import { auth, app } from "../Firebase";
import {
  doc,
  getDoc,
  getFirestore,
  updateDoc,
  setDoc,
  FieldValue,
} from "firebase/firestore";

const FileUpload = (props) => {
  const inputRef = useRef();

  const [selectedFile, setSelectedFile] = useState(null);
  const [progress, setProgress] = useState(0);
  const [uploadStatus, setUploadStatus] = useState("select");
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const navigate = useNavigate();

  const handleFileChange = (event) => {
    if (event.target.files && event.target.files.length > 0) {
      setSelectedFile(event.target.files[0]);
    }
  };

  const onChooseFile = () => {
    inputRef.current.click();
  };

  const clearFileInput = () => {
    inputRef.current.value = "";
    setSelectedFile(null);
    setProgress(0);
    setUploadStatus("select");
  };

  const handleLogin = () => {
    navigate("/login");
  };

  const handleUpload = async () => {
    if (!props.name) {
      navigate("/login");
      return;
    }

    if (uploadStatus === "done") {
      clearFileInput();
      return;
    }

    try {
      setUploadStatus("uploading");

      const formData = new FormData();
      formData.append("file", selectedFile);

      const response = await axios.post(
        "http://localhost:8000/api/upload",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
          onUploadProgress: (progressEvent) => {
            const percentCompleted = Math.round(
              (progressEvent.loaded * 100) / progressEvent.total
            );
            setProgress(percentCompleted);
          },
        }
      );

      const { fileName, fileHash, fileSize } = response.data;

      const user = auth.currentUser;
      if (user) {
        const firestore = getFirestore();
        const userRef = doc(firestore, "users", user.uid);
        const userDocSnap = await getDoc(userRef);

        if (userDocSnap.exists()) {
          // Append the new file details to the existing document
          await updateDoc(userRef, {
            files: app.firestore.FieldValue.arrayUnion({
              fileHash,
              fileName,
              fileSize,
            }),
          }).catch((err) => {
            console.log(err);
          });
        } else {
          // Create a new document with the file details
          await setDoc(userRef, { 
            files: [
              {
                fileHash,
                fileName,
                fileSize,
              },
            ],
          }).catch((err) => {
            console.log(err);
          });
        }
      }
      setUploadedFiles((prevFiles) => [
        ...prevFiles,
        { fileName, fileHash, fileSize },
      ]);
      setUploadStatus("done");
    } catch (error) {
      setUploadStatus("select");
    }
  };

  const handleSignOut = async () => {
    signOut(auth)
      .then(() => {
        navigate("/login");
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <div>
      <header className="header">
        <div className="header-left">
          <h3>
            Welcome{" "}
            {props.name ? (
              props.name
            ) : (
              <span className="text-muted">Guest</span>
            )}
          </h3>
        </div>
        <div className="header-right">
          {props.name ? (
            <button className="btn btn-logout" onClick={handleSignOut}>
              Logout
            </button>
          ) : (
            <button className="btn btn-login" onClick={handleLogin}>
              Sign Up / Login
            </button>
          )}
        </div>
      </header>
      <input
        ref={inputRef}
        type="file"
        onChange={handleFileChange}
        style={{ display: "none" }}
      />
      {/* Button to trigger the file input dialog */}
      {!selectedFile && (
        <button
          className="file-btn"
          onClick={!props.name ? handleLogin : onChooseFile}
        >
          <span className="material-symbols-outlined">upload</span> Upload File
        </button>
      )}
      {selectedFile && (
        <>
          <div className="file-card">
            <span className="material-symbols-outlined icon">description</span>
            <div className="file-info">
              <div style={{ flex: 1 }}>
                <h6>{selectedFile?.name}</h6>
                <div className="progress-bg">
                  <div className="progress" style={{ width: `${progress}%` }} />
                </div>
              </div>
              {uploadStatus === "select" ? (
                <button onClick={clearFileInput}>
                  <span className="material-symbols-outlined close-icon">
                    close
                  </span>
                </button>
              ) : (
                <div className="check-circle">
                  {uploadStatus === "uploading" ? (
                    `${progress}%`
                  ) : uploadStatus === "done" ? (
                    <span
                      className="material-symbols-outlined"
                      style={{ fontSize: "20px" }}
                    >
                      check
                    </span>
                  ) : null}
                </div>
              )}
            </div>
          </div>
          <button className="upload-btn" onClick={handleUpload}>
            {uploadStatus === "select" || uploadStatus === "uploading"
              ? "Upload"
              : "Done"}
          </button>
        </>
      )}
      <UploadedFilesList uploadedFiles={uploadedFiles} />
    </div>
  );
};

export default FileUpload;
