import React, { useState } from 'react';
import { styled } from '@emotion/styled';
import Button from '@material-ui/core/Button';
import LinearProgress from '@material-ui/core/LinearProgress';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';

const Root = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 1rem;
  border-radius: 4px;
  border: 1px dashed #ddd;
  transition: background-color 0.2s ease-in-out;
  &:hover {
    background-color: #f5f5f5;
  }
`;

const InputArea = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 1rem;
`;

const UploadButton = styled(Button)`
  margin-right: 0.5rem;
`;

const ProgressBar = styled(LinearProgress)`
  width: 100%;
  margin-top: 0.5rem;
`;

const DragAreaText = styled.p`
  opacity: 0.5;
  transition: opacity 0.2s ease-in-out;
`;

const FileUpload = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [isUploading, setIsUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);
  };

  const handleUpload = async () => {
    if (!selectedFile) return;

    setIsUploading(true);

    // Simulate upload progress (replace with your backend logic)
    for (let i = 0; i <= 100; i += 10) {
      await new Promise((resolve) => setTimeout(resolve, 100));
      setUploadProgress(i);
    }

    setIsUploading(false);
    setSelectedFile(null);
  };

  const handleDragOver = (event) => {
    event.preventDefault();
    event.dataTransfer.effectAllowed = 'copy';
  };

  const handleDragLeave = (event) => {
    event.preventDefault();
  };

  return (
    <Root onDragOver={handleDragOver} onDragLeave={handleDragLeave}>
      <DragAreaText>Drag and drop files here or</DragAreaText>
      <InputArea>
        <input type="file" onChange={handleFileChange} hidden />
        <UploadButton variant="contained" color="primary" startIcon={<CloudUploadIcon />}>
          Choose File
        </UploadButton>
        <span>{selectedFile ? selectedFile.name : 'No file selected'}</span>
      </InputArea>
      {isUploading && <ProgressBar variant="determinate" value={uploadProgress} />}
    </Root>
  );
};

export default FileUpload;
