import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import LinearProgress from '@material-ui/core/LinearProgress';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    padding: theme.spacing(2),
    borderRadius: theme.shape.borderRadius,
    border: `1px dashed ${theme.palette.grey[300]}`,
    transition: 'background-color 0.2s ease-in-out',
  },
  inputArea: {
    display: 'flex',
    alignItems: 'center',
    marginBottom: theme.spacing(2),
  },
  uploadButton: {
    marginRight: theme.spacing(2),
  },
  progressBar: {
    width: '100%',
    marginTop: theme.spacing(1),
  },
  dragAreaText: {
    opacity: 0.5,
    transition: 'opacity 0.2s ease-in-out',
  },
}));

const FileUpload = () => {
  const classes = useStyles();
  const [selectedFile, setSelectedFile] = useState(null);
  const [isUploading, setIsUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);
  };

  const handleUpload = async () => {
    if (!selectedFile) return;

    setIsUploading(true);

    // Simulate upload progress (replace with your backend logic)
    for (let i = 0; i <= 100; i += 10) {
      await new Promise((resolve) => setTimeout(resolve, 100));
      setUploadProgress(i);
    }

    setIsUploading(false);
    setSelectedFile(null);
  };

  const handleDragOver = (event) => {
    event.preventDefault();
    event.dataTransfer.effectAllowed = 'copy';
    classes.root && (classes.root.style.backgroundColor = '#f5f5f5'); // Highlight on drag over
    classes.dragAreaText && (classes.dragAreaText.style.opacity = 1); // Show drag text
  };

  const handleDragLeave = (event) => {
    event.preventDefault();
    classes.root && (classes.root.style.backgroundColor = null); // Reset background on drag leave
    classes.dragAreaText && (classes.dragAreaText.style.opacity = 0.5); // Hide drag text
  };

  return (
    <div className={classes.root} onDragOver={handleDragOver} onDragLeave={handleDragLeave}>
      <p className={classes.dragAreaText}>Drag and drop files here or</p>
      <div className={classes.inputArea}>
        <input type="file" onChange={handleFileChange} hidden />
        <Button
          variant="contained"
          color="primary"
          className={classes.uploadButton}
          startIcon={<CloudUploadIcon />}
          onClick={() => document.getElementById('fileInput').click()}
        >
          Choose File
        </Button>
        <span>{selectedFile ? selectedFile.name : 'No file selected'}</span>
      </div>
      {isUploading && <LinearProgress className={classes.progressBar} variant="determinate" value={uploadProgress} />}
    </div>
  );
};

export default FileUpload;
